import { TextSequenceStep } from "../../types";

/**
 * Internal helper function to generate text-sequence steps
 * 
 * This is the implementation logic for text-sequence step generation.
 * Exported for use by curriculum-helpers.ts wrapper.
 * 
 * Text-sequence steps display Finglish text and ask the user to select
 * English meanings in the correct order.
 * 
 * Word banks are auto-generated by WordBankService using semantic distractor strategy.
 * 
 * @param finglishText - Finglish phrase to display (e.g., "Salam chetori")
 * @param expectedTranslation - English translation to build (e.g., "Hello how are you")
 * @param points - Points for the text-sequence exercise (default: 3)
 * @param maxWordBankSize - Maximum number of options in word bank (default: 10)
 * @returns TextSequenceStep with finglishText and expectedTranslation
 */
export function textSequenceHelper(
  finglishText: string,
  expectedTranslation: string,
  points: number = 3,
  maxWordBankSize?: number
): TextSequenceStep {
  return {
    type: "text-sequence",
    points,
    data: {
      finglishText,
      expectedTranslation,
      ...(maxWordBankSize !== undefined && { maxWordBankSize })
    }
  };
}

